@import url('https://fonts.googleapis.com/css2?family=Kaushan+Script&family=Poppins:wght@400;600&family=Work+Sans:wght@400;500;600&display=swap');

body {
  margin: 0;
  padding: 0;
  font-family: 'Poppins', sans-serif;
  background: linear-gradient(135deg, #f6f8fc, #e3f2fd);
  background-attachment: fixed;
}

/* ===== Authenticate Page Container ===== */
.AuthenticatePage {
  min-height: 100vh;
  width: 100%;
  padding-top: 10vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-image: url('../images/auth.png');
  background-size: cover;
  background-position: center;
  backdrop-filter: blur(2px);
}

/* ===== Navbar ===== */
.auth-navbar {
  height: 10vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 3vw;
  position: fixed;
  top: 0;
  background-color: #ffffff;
  box-shadow: rgba(0, 0, 0, 0.1) 0px 4px 12px;
  z-index: 999;
}

.auth-navbar h3 {
  font-family: 'Kaushan Script', cursive;
  font-size: 2rem;
  color: crimson;
  cursor: pointer;
}

.auth-navbar p {
  color: crimson;
  font-weight: 600;
  font-size: 1rem;
  cursor: pointer;
}

/* ===== Auth Form Card ===== */
.authForm {
  margin-top: 15vh;
  background: rgba(255, 255, 255, 0.7);
  backdrop-filter: blur(12px);
  border-radius: 12px;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.2);
  width: 90%;
  max-width: 420px;
  padding: 30px;
  text-align: center;
  border: 2px solid #cb7c7c;
  animation: slideIn 0.5s ease;
}

.authForm:hover {
  transform: translateY(-3px);
  transition: 0.3s ease;
}

.authForm h2 {
  color: #0066cc;
  font-size: 1.9rem;
  margin-bottom: 25px;
  font-weight: 600;
}

/* ===== Inputs ===== */
.authFormInputs {
  margin-bottom: 20px;
  text-align: left;
}

.authFormInputs label {
  display: block;
  margin-bottom: 8px;
  color: #003366;
  font-weight: 500;
}

.authFormInputs input,
.authForm select {
  width: 100%;
  padding: 14px 12px;
  border: 1px solid #aad4ff;
  border-radius: 6px;
  background-color: #f8fbff;
  color: #003366;
  font-size: 1rem;
  transition: 0.2s ease-in-out;
}

.authFormInputs input:focus,
.authForm select:focus {
  border-color: #007bff;
  outline: none;
  box-shadow: 0 0 4px #007bff80;
}

/* ===== Button ===== */
.authForm button {
  width: 100%;
  background-color: #007bff;
  color: white;
  padding: 14px;
  font-size: 1rem;
  border: none;
  border-radius: 6px;
  font-weight: bold;
  transition: all 0.3s ease;
}

.authForm button:hover {
  background-color: #0056b3;
  box-shadow: 0 6px 20px rgba(0, 123, 255, 0.3);
}

/* ===== Bottom Text ===== */
.authForm p {
  margin-top: 20px;
  font-size: 0.95rem;
  color: #333;
}

.authForm p span {
  color: #0056b3;
  font-weight: 600;
  cursor: pointer;
  text-decoration: underline;
}

/* ===== Animation ===== */
@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* ===== Responsive Design ===== */
@media screen and (max-width: 768px) {
  .auth-navbar h3 {
    font-size: 1.5rem;
  }

  .auth-navbar p {
    font-size: 0.9rem;
  }

  .authForm {
    margin-top: 13vh;
    width: 90%;
    padding: 25px;
  }

  .authForm h2 {
    font-size: 1.6rem;
  }

  .authFormInputs input,
  .authForm select {
    font-size: 0.95rem;
  }

  .authForm button {
    font-size: 0.95rem;
    padding: 12px;
  }
}

