      ).catch((err)=>{
        console.log(err);
      })
  }

  const fetchApplications = async() =>{
    await axios.get("http://localhost:5500/fetch-applications").then(
      (response)=>{
          setApplicationsCount(response.data.length);
      }
    ).catch((err)=>{
      console.log(err);
    })
}

const fetchUsers = async() =>{
  await axios.get("http://localhost:5500/fetch-users").then(
    (response)=>{
        setUsersCount(response.data.length);
